import axios from 'axios'; 
import TaskDictionary from './TaskDictionary';

export default class DatabaseConnector {

    static async addTask(task) {

        var config = {
            method: 'post',
            maxBodyLength: Infinity,
            url: 'http://localhost:8080/',
            headers: { 
            'Content-Type': 'application/json'
            },
            data : JSON.stringify(task)
        };

        console.log(JSON.stringify(task)); 

        await axios(config).then().catch(err=>console.log(err)); 
    }

    static async getData(task) {
        await axios.get("http://localhost:8080/")
            .then((res) => {
                let date = parseInt(res.date); 
                TaskDictionary.addTask(date, {...res, date:date}); 
            }); 
    }

    //Implement later after u do front end 
    /*
    static updateTask()

    */

}
