import Task from "./Task"; 

export default class TaskDictionary {

    // Key should be date in the form of a int, day/month/year

    static dict = {}; 

    contructor() {}

    static addTask(date, Task) {

        var dict = TaskDictionary.dict; 

        if(dict[date] == null) dict[date] = [Task]; 

        else dict[date].push(Task); 


    }


    static sortDict() {
        var dict = TaskDictionary.dict; 

        dict.sort((a, b) => {

            while(a % 100 === b % 100) {
                a /= 100; 
                b /= 100; 

                if(a === 0) break; 
            }

            return a % 100 - b % 100; 
        }); 
    }

    static getTasks(day) {
        var dict = TaskDictionary.dict; 
        return dict[day]; 
    }

    static convertDate(date) {

        let value = ""; 

        value += date.getDay(); 

        console.log(date); 

        if(date.getMonth() < 10) value += "0" + date.getMonth(); 



        value += date.getYear() % 100; 

        // Returns string, have to parse to get dict data 

        return value; 

    }

    // Returns date given number input 
    static revertDate(number) {

        let year = number % 100 + 2000; 

        number /= 100; 
        let month = number % 100; 
        number /= 100; 
        let day = number % 100; 

        let date = new Date(year, month, day); 

        return date; 




    }


}
